using UnityEngine;using System.Collections;public class GoatScript : MonoBehaviour {    private float life;	// Use this for initialization	void Start () {        life = 4;	}		// Update is called once per frame	void Update () {		}    void OnTriggerEnter(Collider other) {        if (other.gameObject.CompareTag("Vandal")) {			Destroy (other.gameObject);            --life;            if(life == 0) {				Destroy(gameObject);            }                    }    }}